module Main where

main = print $ detect input

detect :: String -> Int
detect = go 0 [] where
  go n xs (y:ys) | length xs == 14 = n
                 | distinct (y:xs) = go (n+1) (y:xs) ys
                 | otherwise = go (n+1) (y:init xs) ys

distinct :: Eq a => [a] -> Bool
distinct [] = True
distinct [x] = True
distinct (x:xs) = x `notElem` xs && distinct xs

input = ""